mapper.update:
UPDATE tb_comn_cd
SET USE_YN = #{useYn}
, MOD_USER = #{modUser}
, MOD_DT = CURRENT_TIMESTAMP
WHERE COM_GRP_CD = #{comGrpCd}
AND COM_CD = #{comCd}

mapper.delete:
UPDATE tb_comn_cd
SET DEL_YN = 'Y'
, MOD_USER = #{modUser}
, MOD_DT = CURRENT_TIMESTAMP
WHERE COM_GRP_CD = #{comGrpCd}

    AND COM_CD = #{comCd}
